import React from 'react'

class BadgesBox extends React.Component {
    render() {
        return (
            <div>
                {this.props.badge.name}
            </div>
        );
    }
}

class BadgesListBox extends React.Component {
    render() {
      if (this.props.badges.length === 0) {
        return (
          <div>
            <h3>No badges were found</h3>
            <button>Crear nuevo Badge</button>
          </div>
        );
      }
  
      return (
        <div className="BadgesList">
          <ul className="list-unstyled">
            {this.props.badges.map(badge => {
              return (
                <li key={badge.id}>
                  <BadgesBox badge={badge} />
                </li>
              );
            })}
          </ul>
        </div>
      );
    }
  }

export default BadgesListBox;
