import React from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import Badge from '../components/Badge';
import Badge2 from '../components/Badge2';
import Badge3 from '../components/Badge3';
import Badge4 from '../components/Badge4';
// import api from '../api';
import './style/Badges.css';
import Loading from '../components/Loading';

class Badges extends React.Component {
  constructor() {
    super();
    this.state = {
      information: [
        {
          "id": 1,
          "name": "Exon",
          "old": "19 años",
          "raza": "Espectro oscuro"
        },
        {
          "id": 2,
          "name": "André",
          "old": "19 años",
          "raza": "Humano"
        },
        {
          "id": 3,
          "name": "Shon",
          "old": "Incierto",
          "raza": "Dragón"
        },
        {
          "id": 4,
          "name": "Fiona",
          "old": "18 años",
          "raza": "Moondana"
        }
      ]
    }
  }
  state = {
    loading: true,
    error: null,
    data: undefined,
  };

  componentDidMount() {
    this.fetchData();
  }

  fetchData = async () => {
    this.setState({ loading: true, error: null });

    try {
      const data = await api.badges.list();
      this.setState({ loading: false, data: data });
    } catch (error) {
      this.setState({ loading: false, error: error });
    }
  };


render() {
  if (this.state.loading === true) {
    return <Loading />;
  }
  // if (this.state.error) {
  //   return <Error />;
  // }

  return (
      <React.Fragment>
            <div className="Badges">
                  <Header />
                
                 <div className="Badges__container" >
                     <div className="Badges__buttons" >
                     <Link to="/badges/second" className="btn btn-primary">
                       <h4>Más personajes</h4>
                     </Link>
                     </div>
                     <div className="Badges__boxing">
                       <Badge  />
                       <Badge2 />
                       <Badge3 />
                       <Badge4 />
                       
                     </div>
                     <center>
                       <h1>Autor@: Hanna Anaya</h1>
                    </center>
                </div>
            </div>
      </React.Fragment>
    );
  }

    
}

export default Badges;
