import React from 'react'
import { Link } from 'react-router-dom';
// import Badges from '../pages/Badges';

class BadgesBox extends React.Component {
    render() {
        return (
            <div>
                {this.props.badge.name}
            </div>
        );
    }
};

function useSearchBadges(badges) {
    const [ query, setQuery ] = React.useState('');
    const [ filterBadges, setFilterBadges ] = React.useState(badges);
    
    React.useMemo(() => {
    const result = badges.filter(badge => {
     return `${badge.name}`
     .toLowerCase()
     .includes(query.toLowerCase());
      });
  
      setFilterBadges(result);
    }, [ badges, query ]);
  
    return { query, setQuery, filterBadges };
  }
  
  function BadgesList (props) {
    const badges = props.badges;
  
    const { query, setQuery, filterBadges } = useSearchBadges(badges)

  
      return (
  
          <ul className="list-unstyled">
            {filterBadges.map(badge => {
              return (
                <li key={badge.id}>
                  <Link
                  className="text-reset text-decoration-none"
                  to={`/badges/${badge.id}`}
                  >
                  <BadgesBox badge={badge}/>
                  </Link>
                </li>
              );
            })}
          </ul>
      );
  }

export default BadgesBox;
