import React from 'react';
import { Link } from 'react-router-dom';
import Loading from '../components/Loading';
import Header from '../components/Header';
import BadgesBox from '../components/BadgesBox';
import ErrorPage from '../components/ErrorPage';
import api from '../api';
import './style/Badges.css';

class Badges extends React.Component {
  state = {
    loading: true,
    error: null,
    data: [
      {
          id: 1,
          name: "Exon",
          old: "19 años",
          raza: "Espectro oscuro"
        },
        {
          id: 2,
            name: "André",
            old: "19 años",
            raza: "Humano"
        },
        {
          id: 3,
            name: "Shon",
            old: "Incierto",
            raza: "Dragón"
        },
        {
          id: 4,
          name: "Fiona",
          old: "18 años",
          raza: "Moondana"
        },
        {
          id: 5,
          name: "Rosalinda",
          old: "17 años",
          raza: "Humana"
        },
        {
          id: 6,
          name: "Lucia",
          old: "18 años",
          raza: "Humana"
        },
        {
          id: 7,
          name: "Yane",
          old: "19 años",
          raza: "Humana"
        },
        {
          id: 8,
          name: "Jack",
          old: "18 años",
          raza: "Humano"
        }
  ]
    }

  componentDidMount() {
    this.fetchData();
  }

  fetchData = async () => {
    this.setState({ loading: true, error: null });

    try {
      const data = await api.badges.list();
      this.setState({ loading: false, data: data });
    } catch (error) {
      this.setState({ loading: false, error: error });
    }
  };


render() {
  if (this.state.loading === true) {
    return <Loading />;
  }
  if (this.state.error) {
    return <ErrorPage />;
  }

  return (
      <React.Fragment>
            <div className="Badges">
                  <Header />
                
                 <div className="Badges__container" >
                     <div className="Badges__buttons" >
                     <Link to="/badges/second" className="btn btn-primary">
                       <h4>Más personajes</h4>
                     </Link>
                     </div>
                     <div className="Badges__boxing"> 
                       <BadgesBox badges={this.state.data} />
                     </div>
                     <center>
                       <h1>Autor@: Hanna Anaya</h1>
                    </center>
                </div>
            </div>
      </React.Fragment>
    );
  }

    
}

export default Badges;
